mapper.insert:
INSERT 
INTO DataDictionary ( 
  Id
  ,Type
  , Code
  , Content
  , ContentExplain
  , IsDel
  , Creater
  , CreaterDate
) 
VALUES ( 
  REPLACE(UUID(),'-','')
  ,	#{Type}
  , #{Code}
  , #{Content}
  , #{ContentExplain}
  , 0
  , #{Creater}
  , NOW()
)

mapper.update:
UPDATE DataDictionary
SET
  Content = #{Content}
  , Reviser = #{Reviser}
  , ReviserDate = NOW()
WHERE
  Id = #{Id}
