mapper.insert:
INSERT 
	INTO truckscale.systemsetup( 
	    TruckScaleType		
	  , SerialPort			
	  , BaudRate 			
	  , WeighingMethod 		
	  , SaveSettings 		
	  , MaterialSelection	
	  , IsDel
	  , Creater
	  , CreaterDate
	) 
	VALUES ( 
		#{TruckScaleType}		
	  , #{SerialPort}			
	  , #{BaudRate} 			
	  , #{WeighingMethod} 		
	  , #{SaveSettings} 		
	  , #{MaterialSelection}	
	  , 0
	  , #{Creater}
	  , NOW() 
	)

mapper.update:
UPDATE  truckscale.systemsetup 
SET
	TruckScaleType = #{TruckScaleType}		
  , SerialPort = #{SerialPort}			
  , BaudRate = #{BaudRate} 			
  , WeighingMethod = #{WeighingMethod} 		
  , SaveSettings = #{SaveSettings} 		
  , MaterialSelection = #{MaterialSelection}	
  , IsDel = 0
  , Reviser = #{Creater}
  , ReviserDate = NOW()
