mapper.select:
select cpd.Org_ID ,cpd.ProdPlan_No ,cpd.Equ_ID ,cpd.Bin_Relationship_ID ,e.Equipment_Name ,CONVERT(varchar(20),cpd.Collect_Date,120) collect_Date ,cpd.Mix_Time ,cpd.Grade ,cpd.Disk_Number ,cpd.Weight_Cement1 ,cpd.Weight_Cement2 ,cpd.Weight_Cement3 ,cpd.Weight_Cement4 ,cpd.Weight_Aggregate1 ,cpd.Weight_Aggregate2 ,cpd.Weight_Aggregate3 ,cpd.Weight_Aggregate4 ,cpd.Weight_Aggregate5 ,cpd.Weight_Aggregate6 ,cpd.Weight_Water ,cpd.Weight_Admixture1 ,cpd.Weight_Admixture2 ,cpd.Weight_Cement1 ,cpd.Weight_Cement2 ,cpd.Weight_Cement3 ,cpd.Weight_Cement4 ,cpd.Set_Aggregate1 ,cpd.Set_Aggregate2 ,cpd.Set_Aggregate3 ,cpd.Set_Aggregate4 ,cpd.Set_Aggregate5 ,cpd.Set_Aggregate6 ,cpd.Set_Water ,cpd.Set_Admixture1 ,cpd.Set_Admixture2 ,cpd.Total_Weight ,cpd.Mixing_Volume ,cpp.Proj_Pos ,cpp.Watering_Site ,cpd.Analysis_Results from Cement_ProductionData cpd left join Cement_Production_Plan cpp on cpd.ProdPlan_No = cpp.Plan_No and cpp.Valid_Flag = 1 left join Equipment_Info e on e.Id = cpd.Equ_ID and e.Valid_Flag = 1 where cpd.Valid_Flag = 1
AND e.Equipment_Name like '%${mixingEquipmentName}%'
AND cpd.Analysis_Results LIKE '%${analysisResults}%'
AND cpd.Collect_Date >= #{startTime}
AND cpd.Collect_Date <= #{endTime}
AND cpd.Org_ID = #{orgId}
order by cpd.collect_Date desc
